
public class CP_SP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int cp=129;
		int sp=191;
		int profit=sp-cp;
		double p_percent=(profit/cp)*100;
		System.out.print("The Cost Price is INR " + cp + " and Selling Price is INR " + sp +
	            "\nThe Profit is INR " + profit + " and the Profit Percentage is " + p_percent);

	}

}
